import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const ViewConfig = () => {
  const [config, setConfig] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // ดึงค่าจาก .env
  const apiUrl = process.env.REACT_APP_API_URL;
  const droneId = parseInt(process.env.REACT_APP_DRONE_ID); // ได้ค่า 3001 จาก .env

  useEffect(() => {
    const fetchConfig = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const response = await axios.get(`${apiUrl}/configs/${droneId}`);
        
        // ตรวจสอบว่ามีข้อมูลจริง
        if (!response.data) {
          throw new Error('ไม่พบข้อมูลการตั้งค่า');
        }

        setConfig(response.data);
      } catch (err) {
        console.error('Error fetching config:', {
          url: `${apiUrl}/configs/${droneId}`,
          status: err.response?.status,
          error: err.message
        });
        setError(`ไม่สามารถโหลดข้อมูล: ${err.response?.data?.message || err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchConfig();
  }, [apiUrl, droneId]);

  // ... ส่วนแสดงผลเหมือนเดิม ...
};

export default ViewConfig;
