import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const ViewConfig = () => {
  const [config, setConfig] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // 1. ดึงค่าจาก .env และตรวจสอบค่า
  const apiUrl = process.env.REACT_APP_API_URL || 'https://server-with-node-js-server.vercel.app';
  const droneId = parseInt(process.env.REACT_APP_DRONE_ID) || 3001; // ค่าเริ่มต้นเป็น 3001 หากอ่านจาก .env ไม่ได้

  useEffect(() => {
    // 2. ตรวจสอบค่าก่อนเรียก API
    console.log('Current Config:', {
      apiUrl,
      droneId,
      envDroneId: process.env.REACT_APP_DRONE_ID // ตรวจสอบค่าจาก .env โดยตรง
    });

    const fetchConfig = async () => {
      try {
        setLoading(true);
        setError(null);
        
        // 3. เรียก API พร้อม headers
        const response = await axios.get(`${apiUrl}/configs/${droneId}`, {
          headers: {
            'Accept': 'application/json'
          }
        });

        console.log('API Response:', response.data); // Log response

        // 4. ตรวจสอบโครงสร้างข้อมูล
        if (!response.data) {
          throw new Error(`ไม่พบการตั้งค่าสำหรับ Drone ID: ${droneId}`);
        }

        // 5. ตรวจสอบว่าได้ Config ที่ถูกต้อง
        if (response.data.drone_id !== droneId) {
          throw new Error(`ได้รับข้อมูลผิด Drone ID (Expected: ${droneId}, Got: ${response.data.drone_id})`);
        }

        setConfig(response.data);
      } catch (err) {
        console.error('Error Details:', {
          url: `${apiUrl}/configs/${droneId}`,
          status: err.response?.status,
          error: err.message,
          responseData: err.response?.data
        });
        
        setError(`ไม่สามารถโหลดข้อมูล: ${err.response?.data?.message || err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchConfig();
  }, [apiUrl, droneId]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center mt-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <p className="ms-3">กำลังโหลดข้อมูล Drone ID: {droneId}...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger">
          <h5>เกิดข้อผิดพลาด</h5>
          <p>{error}</p>
          <div className="mt-3">
            <button 
              className="btn btn-outline-danger me-2"
              onClick={() => window.location.reload()}
            >
              ลองอีกครั้ง
            </button>
            <button
              className="btn btn-outline-secondary"
              onClick={() => navigate('/')}
            >
              กลับหน้าหลัก
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <div className="card shadow">
        <div className="card-header bg-primary text-white">
          <h2>การตั้งค่า Drone (ID: {droneId})</h2>
        </div>
        <div className="card-body">
          {config && (
            <div className="row">
              <div className="col-md-6 mb-3">
                <h5 className="fw-bold">ชื่อ Drone:</h5>
                <p className="fs-5">{config.drone_name || '-'}</p>
              </div>
              <div className="col-md-6 mb-3">
                <h5 className="fw-bold">ประเทศ:</h5>
                <p className="fs-5">{config.country || '-'}</p>
              </div>
              <div className="col-md-6 mb-3">
                <h5 className="fw-bold">แสงสว่าง:</h5>
                <p className="fs-5">{config.light || '-'}</p>
              </div>
              <div className="col-md-6 mb-3">
                <h5 className="fw-bold">สถานะ:</h5>
                <p className="fs-5">{config.status || '-'}</p>
              </div>
            </div>
          )}
          <div className="mt-4">
            <button 
              onClick={() => navigate('/')}
              className="btn btn-secondary"
            >
              กลับหน้าหลัก
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewConfig;
