import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { 
  Container, 
  Table, 
  Spinner, 
  Alert, 
  Button,
  Badge,
  Card
} from 'react-bootstrap';
import { 
  BsClockHistory,
  BsGeoAlt,
  BsDrone,
  BsPerson,
  BsThermometer,
  BsArrowCounterclockwise,
  BsHouse
} from 'react-icons/bs';

const ViewLogs = () => {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const apiUrl = process.env.REACT_APP_API_URL;
  const droneId = 65011012;

  useEffect(() => {
    const fetchLogs = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const response = await axios.get(`${apiUrl}/logs`, {
          headers: {
            'Authorization': 'Bearer 20250301efx'
          }
        });

        if (!response.data || !Array.isArray(response.data)) {
          throw new Error('Invalid data format');
        }

        const filteredLogs = response.data
          .filter(log => log.drone_id === droneId)
          .sort((a, b) => new Date(b.created) - new Date(a.created))
          .slice(0, 25);
        
        setLogs(filteredLogs);
      } catch (err) {
        console.error('Error fetching logs:', err);
        setError(`Error: ${err.response?.data?.message || err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchLogs();
  }, [apiUrl, droneId]);

  const formatDate = (dateString) => {
    try {
      return new Date(dateString).toLocaleString('th-TH');
    } catch {
      return dateString;
    }
  };

  return (
    <Container className="py-4" style={{ 
      backgroundColor: '#fff5f9',
      minHeight: '100vh'
    }}>
      <Card className="border-0 shadow-sm mb-4" style={{ borderRadius: '15px' }}>
        <Card.Header className="py-3" style={{ 
          backgroundColor: '#ffd6e7',
          borderBottom: 'none'
        }}>
          <div className="d-flex justify-content-between align-items-center">
            <h2 className="mb-0 d-flex align-items-center" style={{ color: '#d63384' }}>
              <BsClockHistory className="me-2" size={28} />
              ประวัติการทำงาน Drone
            </h2>
            <Button 
              variant="outline-primary"
              onClick={() => navigate('/')}
              style={{ 
                color: '#d63384',
                borderColor: '#d63384'
              }}
            >
              <BsHouse className="me-1" />
              กลับหน้าหลัก
            </Button>
          </div>
        </Card.Header>

        <Card.Body>
          {loading ? (
            <div className="d-flex justify-content-center py-5">
              <Spinner animation="border" style={{ color: '#d63384' }} />
            </div>
          ) : error ? (
            <Alert variant="danger" className="rounded-pill">
              <Alert.Heading style={{ color: '#d63384' }}>เกิดข้อผิดพลาด!</Alert.Heading>
              <p>{error}</p>
              <Button 
                variant="outline-danger"
                onClick={() => window.location.reload()}
                className="mt-2"
              >
                <BsArrowCounterclockwise className="me-1" />
                ลองใหม่
              </Button>
            </Alert>
          ) : (
            <div className="table-responsive">
              <Table hover className="mb-0">
                <thead style={{ 
                  backgroundColor: '#ffd6e7',
                  color: '#d63384'
                }}>
                  <tr>
                    <th className="d-flex align-items-center">
                      <BsClockHistory className="me-2" />
                      วันที่บันทึก
                    </th>
                    <th className="d-flex align-items-center">
                      <BsGeoAlt className="me-2" />
                      ประเทศ
                    </th>
                    <th className="d-flex align-items-center">
                      <BsDrone className="me-2" />
                      Drone ID
                    </th>
                    <th className="d-flex align-items-center">
                      <BsPerson className="me-2" />
                      ชื่อ Drone
                    </th>
                    <th className="d-flex align-items-center">
                      <BsThermometer className="me-2" />
                      อุณหภูมิ (°C)
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {logs.length > 0 ? (
                    logs.map((log, index) => (
                      <tr key={index}>
                        <td>{formatDate(log.created)}</td>
                        <td>
                          <Badge pill bg="info">
                            {log.country || '-'}
                          </Badge>
                        </td>
                        <td>
                          <Badge pill style={{ 
                            backgroundColor: '#d63384',
                            fontSize: '0.9rem'
                          }}>
                            {log.drone_id || '-'}
                          </Badge>
                        </td>
                        <td>{log.drone_name || '-'}</td>
                        <td>
                          <Badge pill bg={log.celsius > 30 ? 'danger' : 'success'}>
                            {log.celsius || '-'}°C
                          </Badge>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center py-5">
                        <div className="text-muted mb-3">ไม่พบข้อมูลการทำงาน</div>
                        <Button 
                          variant="outline-primary"
                          onClick={() => window.location.reload()}
                        >
                          <BsArrowCounterclockwise className="me-1" />
                          โหลดใหม่
                        </Button>
                      </td>
                    </tr>
                  )}
                </tbody>
              </Table>
            </div>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default ViewLogs;
